sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		this.rightnow = new GlideDateTime(); 
	},
    isDatePast: function(strDate) {
        var testdata = new GlideDateTime(strDate);
        if (testdata.before(this.rightnow)) {
            return true;
        } else {
            return false;
        }
    },
	isDateToday: function(strDate) {
		var today = this.rightnow.getLocalDate();
		var istoday = new GlideDateTime(strDate).getLocalDate();
		if (today.compareTo(istoday) === 0) {
			return true;
		} else {
			return false;
		}
	},
    type: 'NeedItUtils'
};
